import React from 'react'
import axios from 'axios'
import NavLink from './nav_link'
import Background from "./img/Banners/AAC-insights.jpg"
import ArticlePreview from './ArticlePreview'
import "./scss/insights.css"

export default class Insights extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: []
    };
  }
  componentDidMount() {
    axios
      .get(
        "http://public-api.wordpress.com/rest/v1/sites/agileadvantage462538617.wordpress.com/posts"
      )
      .then(res => {
        this.setState({ posts: res.data.posts });
        console.log(this.state.posts);
      })
      .catch(error => console.log(error));
  }

  render() {
    return (
      <div className="blog">
      <div className="mainpage-banner page" style={{backgroundImage: `url(${Background}`, backgroundPosition: "10% 70%"}}>
        <h1 className="mainpage-banner-text">Expert analysis and contextual reflections to create better businesses in a global market</h1>
      </div>
      {this.state.posts.map((post) => (
        <NavLink className="single-person" to={{
          pathname: `/team/${post.ID}`
        }}>
        </NavLink>
      ))}
      </div>
    );
  }
}
