import React from 'react'
import axios from 'axios'
import swal from 'sweetalert'

export default class Contact extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      windowWidth: 0,
      isMobile: false
    }
  }
  onResize() {
    this.setState({
      windowWidth: window.document.documentElement.clientWidth
    });
    if (this.state.windowWidth < 700) {
      this.setState({
        isMobile: true
      });
    }
    else {
      this.setState({
        isMobile: false
      });
    }
    console.log(isMobile);
    // if (document.getElementsByClassName('contact-details') != "undefined") {
    //   if (this.windowWidth < 750 && document.getElementsByClassName('contact-details')) {
    //     document.getElementsByClassName('contact-details')[0].style.position = "relative";
    //     document.getElementsByClassName('contact-details')[0].style.width = "95%";
    //     document.getElementsByClassName('contact-details')[0].style.marginBottom = "5%";
    //     document.getElementsByClassName('contact-content')[0].style.width = "95%";
    //     document.getElementById('contact-form').style.width = "60%";
    //   }
    //   else {
    //     document.getElementsByClassName('contact-page-details')[0].style.position = "absolute";
    //     document.getElementsByClassName('contact-page-details')[0].style.width = "10%";
    //     document.getElementsByClassName('contact-content')[0].style.width = "70%";
    //     document.getElementById('contact-form').style.width = "50%";
    //   }
    // }
  }
  componentDidMount() {
    window.scrollTo(0, 0);
    this.setState({
      windowWidth: window.document.documentElement.clientWidth
    });
    // this.windowWidth = window.document.documentElement.clientWidth;
    // window.addEventListener("resize", this.onResize);
    // if (this.windowWidth < 750) {
    //   document.getElementsByClassName('contact-details')[0].style.position = "relative";
    //   document.getElementsByClassName('contact-details')[0].style.width = "95%";
    //   document.getElementsByClassName('contact-content')[0].style.width = "95%";
    //   document.getElementById('contact-form').style.width = "60%";
    // }
    // else {
    //   document.getElementsByClassName('contact-details')[0].style.position = "absolute";
    //   document.getElementsByClassName('contact-details')[0].style.width = "10%";
    //   document.getElementsByClassName('contact-content')[0].style.width = "70%";
    //   document.getElementById('contact-form').style.width = "50%";
    // }
  }
  handleSubmit(e){
      e.preventDefault();
      const name = document.getElementById('name').value;
      const email = document.getElementById('email').value;
      const message = document.getElementById('message').value;
      axios({
          method: "POST",
          url:"localhost:3002/send",
          data: {
              name: name,
              email: email,
              message: message
          }
      }).then((response)=>{
          if (response.data.msg === 'success'){
              swal ( "Thank you!", "Your message has been sent." ,  "success" );
              this.resetForm();
          }else if(response.data.msg === 'fail'){
              swal ( "Something went wrong." ,  "Please, check your internet connection." ,  "error" );
          }
      })
  }
  resetForm(){
      document.getElementById('contact-form').reset();
  }
  render() {
    return (
      <div className="contact-page">
        <div className="mainpage-banner page contact-banner" data-aos="fade-down">
          <h1 className="mainpage-banner-text">In a global society, communication is the key</h1>
        </div>
        <div className={this.state.isMobile ? "contact-details" : "mobile"}>
          <h2>Agile Advantage Consulting DMCC</h2>
          <p>Unit No: 1589</p>
          <p>DMCC Business Centre</p>
          <p>Level No 1 Jewellery & Gemplex, 3</p>
          <p>Dubai</p>
          <p>United Arab Emirates</p>
          <p className="spaced">+971 (0) 55 293 2600</p>
          <p className="spaced">info@agileadvantage.consulting</p>
        </div>
        <div className="contact-content">
        <h1 className="gray-header">CONTACT</h1>
        <form id="contact-form" method="POST" method="POST" action="https://formspree.io/info@agileadvantage.consulting">
            <div className="name-fields">
              <div className="form-group">
                  <input type="text" className="form-control" placeholder="FIRST NAME" id="name" />
              </div>
              <div className="form-group">
                  <input type="text" className="form-control" placeholder="LAST NAME" id="name" />
              </div>
            </div>
            <div className="name-fields">
              <div className="form-group">
                  <input type="email" className="form-control" placeholder="EMAIL ADDRESS" id="email" aria-describedby="emailHelp" />
              </div>
              <div className="form-group">
                  <input type="phone" className="form-control" placeholder="PHONE NUMBER" id="phone" />
              </div>
            </div>
            <div className="form-group company-name">
              <input type="text" className="company-name" placeholder="COMPANY NAME" id="company" />
            </div>
            <div className="form-group message-field">
                <label className="message">MESSAGE</label>
                <textarea className="form-control" rows="5" id="message"></textarea>
            </div>
            <div className="contact submit">
              <button type="submit" className="btn btn-primary">SUBMIT</button>
            </div>
        </form>
        </div>
      </div>
    )
  }
}
