import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom'
import SingleArticle from './SingleArticle'
import Background from './img/Banners/AAC-insights.jpg'
import './scss/insights.css'

export default class Insights extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      chosenFilter: "",
      tags: ["insights", "trends", "growth", "news", "strategy", "education"]
    };
  }
  componentDidMount() {
    axios
      .get(
        "https://public-api.wordpress.com/rest/v1/sites/agileadvantage462538617.wordpress.com/posts"
      )
      .then(res => {
        this.setState({ posts: res.data.posts, });
      })
      .catch(error => console.log(error));
  }
  filterPosts(event) {
    // var filteredPosts = this.state.posts;
    // var chosenTag = event.target.innerHTML.toLowerCase();
    // for (var i = 0; i < filteredPosts.length; i++) {
    //   var individualTags = Object.keys(filteredPosts[i].tags);
    //   if (!individualTags.includes(chosenTag)) {
    //     filteredPosts.splice(i, 1);
    //   }
    // }
    // this.setState({filteredPosts: filteredPosts});
    // console.log(filteredPosts);

  }
  handleTagChange(event) {
    var chosenTag = event.target.innerHTML.toLowerCase();
    this.setState({ chosenFilter: chosenTag});
  }
  getAllTags() {
    var posts = this.state.posts;
    var tags = {
      insights: 0,
      trends: 0,
      growth: 0,
      news: 0,
      strategy: 0,
      education: 0
    }
    for (var i = 0; i < posts.length; i++) {
      var individualTags = posts[i].tags;
      var numTags = Object.keys(individualTags).length;
      for (var j = 0; j < numTags; j++){
        if (!(Object.keys(individualTags).length === 0 && individualTags.constructor === Object)) {
          var tagName = individualTags[Object.keys(individualTags)[j]].name;
          switch(tagName) {
            case "insights":
              tags.insights ++;
              break;
            case "trends":
              tags.trends ++;
              break;
            case "growth":
              tags.growth ++;
              break;
            case "news":
              tags.news ++;
              break;
            case "strategy":
              tags.strategy ++;
              break;
            case "education":
              tags.education ++;
              break;
            default:
              break;
          }
        }
      }
    }
    return tags;
  }
  render() {
    var tags = this.getAllTags();
    var filteredPosts = this.state.posts.filter(post => {
      var indTags = Object.keys(post.tags);
      return (indTags.search(this.state.chosenFilter) !== 1);
    })
    return (
      <div className="blog">
      <div className="mainpage-banner page" style={{backgroundImage: `url(${Background})`, backgroundPosition: "10% 70%"}}>
        <h1 className="mainpage-banner-text">Change is inevitable, progress is a choice.</h1>
      </div>
      <div className="blogpage-content">
      <ul className="tags">
        {this.state.tags.map((tag) => (
          <div key={tag}>
            {tags[Object.keys(tags)[Object.keys(tags).indexOf(tag)]] ?
            <div className="tag" key={tag} onClick={this.handleTagChange.bind(this)}>
              <h2>{tag.toUpperCase()}</h2>
              <h2>({tags[Object.keys(tags)[Object.keys(tags).indexOf(tag)]]})</h2>
            </div>
            : <div/>
            }
          </div>
        ))}
      </ul>
      <ul className="articles-list">
      {this.state.posts.map((post) => (
        <Link key = {post.ID} className={"single-service " + post.ID} to={{
          pathname: `/blog/${post.ID}`,
          state: {
            fromParent: post
          }
        }}>
        <img src={post.featured_image} alt="article-img"/>
        <span>{post.title}</span>
        </Link>
      ))}
      </ul>
      </div>
      </div>
    );
  }
}
