import React from 'react'
import { Link } from 'react-router-dom'
import Background from './img/Banners/AAC-services.jpg'
import {services1, services2} from './data/all-services'
import companyLogos from './data/all-companies'
import './scss/services.css'

export default class Services extends React.Component {
  componentDidMount() {
    window.scrollTo(0,0);
  }
  render() {
    return (
      <div className="mainpage">
      <div className="mainpage-banner page long" style={{backgroundImage: `url(${Background})`}}>
        <h1 className="mainpage-banner-text long">When we get the environment right, humans will do remarkable things.</h1>
      </div>
      <div className="page-content">
        <li className="services-list">
        {services1.map(({name, id, image, description}) => (
            <Link to="services/{id}"/>
          ))}
        </li>
        <h2>Some companies we have worked with in the past:</h2>
        <div className="past-companies">
          <ul className="companies-list">
          {companyLogos.map(({image}) => (
            <img src={require(`./img/CompanyLogos/${image}`)} alt="company-logo" width="100px"/>
          ))}
          </ul>
        </div>
      </div>
      </div>
    )
  }
}
