import React from 'react'

export default class SingleArticle extends React.Component {
  removeUnicode(string) {
    if (string.indexOf("&#8217;") >= 0) {
      return this.removeUnicode(string.replace("&#8217;", "'"));
    } else {
      return string.replace("<p>", "").replace("[&hellip;]</p>", "...");
    }
  }
  configureImage(string) {
    return string.replace("<p>", "").replace("</p>", "");
  }
  render() {
    var title = this.props.location.state.fromParent.title;
    var description = this.removeUnicode(this.props.location.state.fromParent.excerpt);
    var image = this.props.location.state.fromParent.featured_image;
console.log(image);
    return (
      <div className="single-article">
      {title}
      {description}
      {image ? (
        <img
          className="img-responsive webpic"
          alt="article header"
          src={image}
        />
      ) : (
        ""
      )}
      </div>
    )
  }
}
