import React from 'react'
import logo from './img/logo.png'
import search from './img/search.png'
import NavLink from './nav_link'
import './scss/header.css'

var windowWidth;
var isMobile;
class Header extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isActive: false
    }
  }
  onResize() {
    this.windowWidth = window.document.documentElement.clientWidth;
    if (this.windowWidth < 800) {
      document.getElementsByClassName('header-logo')[0].style.display = "none";
    }
    else {
      document.getElementsByClassName('header-logo')[0].style.display = "block";
    }
  }
  componentDidMount() {
    this.windowWidth = window.document.documentElement.clientWidth;
    window.addEventListener("resize", this.onResize);
  }
  toggleNav = () => {
    this.setState(prevState => ({
      isActive: !prevState.isActive
    }))
  }

  render() {
    var isMobile = this.windowWidth < 800;
    console.log(isMobile);
    return (
      <div className="header">
        <NavLink to="/" className="logo">
          <img className="header-logo" src={logo} alt="logo"/>
        </NavLink>
        <div className="navigation">
        <div className="nav-items">
          <NavLink to="/about"><span>ABOUT</span></NavLink>
          <NavLink to="/services"><span>SERVICES</span></NavLink>
          <NavLink to="/insights"><span>INSIGHTS</span></NavLink>
          <NavLink to="/team"><span>TEAM</span></NavLink>
          <NavLink to="/community"><span>COMMUNITY</span></NavLink>
          <NavLink to="/contact"><span>CONTACT</span></NavLink>
        </div>
        </div>
      </div>
    )
  }
}

export default Header

