import React from 'react'

export default class SingleService extends React.Component {
  render() {
    console.log(this.props.location);
    var name = this.props.location.state.fromParent.name;
    var image = this.props.location.state.fromParent.image;
    var description = this.props.location.state.fromParent.description;
    return (
      <div className="single-service-content">
        <h1>{name}</h1>
        <img className="mainpage-banner big" key = {image} src = {require(`./img/Banners/AAC-services-${image}`)} alt = ""/>
        <p>{description}</p>
      </div>
    )
  }
}
