import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom'
import SingleArticle from './SingleArticle'
import Background from './img/Banners/AAC-insights.jpg'
import './scss/insights.css'

export default class Insights extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: [],
      tags: []
    };
  }
  componentDidMount() {
    axios
      .get(
        "https://public-api.wordpress.com/rest/v1/sites/agileadvantage462538617.wordpress.com/posts"
      )
      .then(res => {
        this.setState({ posts: res.data.posts });
      })
      .catch(error => console.log(error));
      this.getAllTags();
  }
  getAllTags() {
    var tags = []
    var posts = this.state.posts
    for (var i = 0; i < posts.length; i++) {
      var individualTags = posts[i].tags;
        if (!(Object.keys(individualTags).length === 0 && individualTags.constructor === Object) && !(tags.indexOf(individualTags) >= 0)) {
          tags.push(individualTags);
        }
    }
    return tags;
  }
  render() {
    var tags = this.getAllTags();
    console.log(tags[0]);
    return (
      <div className="blog">
      <div className="mainpage-banner page" style={{backgroundImage: `url(${Background})`, backgroundPosition: "10% 70%"}}>
        <h1 className="mainpage-banner-text">Change is inevitable, progress is a choice.</h1>
      </div>
      <div className="blogpage-content">
      <ul  className="tags">

      </ul>
      <ul className="articles-list">
      {this.state.posts.map((post) => (
        <Link key = {post.ID} className={"single-service " + post.ID} to={{
          pathname: `/blog/${post.ID}`,
          state: {
            fromParent: post
          }
        }}>
        <img src={post.featured_image}/>
        <span>{post.title}</span>
        </Link>
      ))}
      </ul>
      </div>
      </div>
    );
  }
}
