import React from 'react';
import ReactDOM from 'react-dom';
import { BrowserRouter as Router } from 'react-router-dom'
import './app/layout/scss/index.css';
import App from './App';
import * as serviceWorker from './serviceWorker';
import DataActions from './app/flux/actions/DataActions.js'


ReactDOM.render((
    <Router>
    <App />
    </Router>
), document.getElementById('root'));

var express = require('express');
var router = express.Router();
var nodemailer = require('nodemailer');
const creds = require('../config/config');

var transport = {
  host: 'mail.agileadvantage.consulting',
  port: 587,
  secure: false, // true for 465, false for other ports
  auth: {
    user: creds.USER, // your domain email address
    pass: creds.PASS // your password
  },
  tls: {
    rejectUnauthorized: false
  }
}

var transporter = nodemailer.createTransport(transport)

transporter.verify((error, success) => {
  if (error) {
    console.log(error);
  } else {
    console.log('Server is ready to take messages');
  }
});

router.post('/send', (req, res, next) => {
  var name = req.body.name
  var email = req.body.email
  var message = req.body.message
  console.log(message);
  var content = `name: ${name} \n email: ${email} \n message: ${message} `

  var mail = {
    from: '"Agile Advantage" info@agileadvantage.consulting',
    to: 'info@agileadvantage.consulting',  //Change to email address that you want to receive messages on
    subject: 'New Message from Contact Form',
    text: content
  }

  transporter.sendMail(mail, (err, data) => {
    if (err) {
      res.json({
        msg: 'fail'
      })
    } else {
      res.json({
        msg: 'success'
      })
    }
  })
})

module.exports = router;

// If you want your app to work offline and load faster, you can change
// unregister() to register() below. Note this comes with some pitfalls.
// Learn more about service workers: http://bit.ly/CRA-PWA
serviceWorker.register();
