import React from 'react'
import {people1, people2} from './data/all-people'

export default class SingleArticle extends React.Component {
  removeUnicode(string) {
    if (string.indexOf("&#8217;") >= 0) {
      return this.removeUnicode(string.replace("&#8217;", "'"));
    } else {
      return string.replace("<p>", "").replace("</p>", "").replace("[&hellip;]</p>", "...").replace("&nbsp;", "");
    }
  }
  configureImage(string) {
    return string.replace("<p>", "").replace("</p>", "");
  }
  getAuthorDetails() {
    var job_image = []
    for (var i=0; i < people1.length; i++) {
      if (people1[i].name === "ROB HOULDSWORTH") {
        job_image[0] = people1[i].job;
        job_image[1] = people1[i].image;
      }
      else if (people2[i].name === "ROB HOULDSWORTH") {
        job_image[0] = people2[i].job;
        job_image[1] = people2[i].image;
      }
    }
    return job_image;
  }
  render() {
    var authorJob = this.getAuthorDetails()[0];
    var authorImage = this.getAuthorDetails()[1];
    let self = this.props.location.state.fromParent;
    var title = self.title;
    var description = this.removeUnicode(self.excerpt);
    var image = self.featured_image;
    var tags = self.tags;
    console.log(authorImage);
    return (
      <div className="single-article">
      <div className="mainpage-banner page" style={{backgroundImage: `url(${image})`}}>
      </div>
      <div className="contact-details">
        <div className="contact-image"style={{backgroundImage: `url(${require(`./img/People/${authorImage}`)})`}}/>
        <h2>ROB SOMEONE</h2>
        <p>{authorJob}</p>
        <div className="contact">
        <button>CONTACT</button>
        </div>
      </div>
      <div className="single-article-content">
        <h1 className="gray-header">{title.toUpperCase()}</h1>
        <div className="article-summary">Summary</div>
        <div className="article-data">
          <span>BY:<h3>ROB SOMEONE</h3></span>
          <span>PUBLISHED:<h3>23 Nov 2018</h3></span>
        </div>
        <p>{description}</p>
      </div>
      </div>
    )
  }
}
