import React, { Component } from "react";
import axios from "axios";
import { Link } from 'react-router-dom'
import SingleArticle from './SingleArticle'

export default class Insights extends Component {
  constructor(props) {
    super(props);
    this.state = {
      posts: []
    };
  }
  componentDidMount() {
    console.log(this.state.posts);
    axios
      .get(
        "https://public-api.wordpress.com/rest/v1/sites/agileadvantage462538617.wordpress.com/posts"
      )
      .then(res => {
        console.log(res.data.posts);
        this.setState({ posts: res.data.posts });
      })
      .catch(error => console.log(error));
  }

  render() {
    console.log(this.state.posts);
    return (
      <div className="blog">
        <div className="anchor" id="blog" />
        <h1 className="sectionTitle">Articles</h1>
        <li className="services-list">
          {this.state.posts.map((post) => (
            <SingleArticle post={post}/>
          ))}
        </li>
      </div>
    );
  }
}
