import React from 'react'
import { Link } from 'react-router-dom'
import './scss/homepage.css'
import Background from "./img/Banners/AAC-main.jpg"

var windowWidth = 0;
export default class Home extends React.Component {
    onResize() {
      this.windowWidth = window.document.documentElement.clientWidth;
    }
    componentDidMount() {
      window.scrollTo(0,0);
      this.windowWidth = window.document.documentElement.clientWidth;
      window.addEventListener("resize", this.onResize);
      console.log(this.windowWidth);
    }
    render() {
      return(
        <div className="mainpage">
          <div className="mainpage-banner" data-aos="fade-down" style={{backgroundImage: `url(${Background})`}}>
            <h1 className="mainpage-banner-text">Management & strategy consulting firm based in Dubai</h1>
          </div>
          <div className={"homepage-description" + (this.windowWidth < 700) ? "" : " mobile"}>
            <p> <span className="bolder">Agile Advantage Consulting (AA)</span> is a management and strategy consulting
            firm specialising in delivering efficient and
            effective professional services to assure high-performance
            in our clientsâ€™ organisations. Founded by Steve Biring <Link to="/about" style = {{textDecoration: "none"}}> >> </Link>
            </p>
          </div>
          <h1 className="white-heading" data-aos="fade-right">INSIGHTS</h1>
          <div className="homepage-services">
            <h1>Services we offer</h1>
            <li className="services-list" data-aos="fade-up">
              <div className="single-service analytics" style= {{ opacity: 0.8}}>
                <span>ADVANCED ANALYTICS</span>
              </div>
              <div className="single-service change" style= {{ opacity: 0.8}}>
                <span>CHANGE MANAGEMENT</span>
              </div>
              <div className="single-service finance" style= {{ opacity: 0.8}}>
                <span>CORPORATE FINANCE</span>
              </div>
            </li>
          </div>
        </div>
      )
    }
}
