import React from 'react'
import { Link } from 'react-router-dom'
import {services1, services2} from './data/all-services'
import companyLogos from './data/all-companies'
import './scss/services.css'

export default class Services extends React.Component {
  state = {
    background: ""
  }
  componentWillMount() {
    this.setState({
      background: require('./img/Banners/AAC-services.jpg')
    });
  }
  componentDidMount() {
    window.scrollTo(0,0);
  }
  render() {
    return (
      <div className="mainpage">
      <div className="mainpage-banner page long" data-aos="fade-down" style={{backgroundImage: `url(${this.state.background})`}}>
        <h1 className="mainpage-banner-text long">When we get the environment right, humans will do remarkable things.</h1>
      </div>
      <div className="page-content">
        <li className="services-list" data-aos="fade-up">
        {services1.map(({name, id, image, description}) => (
            <Link key = {id} className={"single-service " + id} to={{
              pathname: `/services/${id}`,
              state: {
                fromParent: {name, id, description}
              }
            }}>
              <span>{name}</span>
            </Link>
          ))}
        </li>
        <li className="services-list" data-aos="fade-up">
        {services2.map(({name, id, image, description}) => (
            <Link key = {id} className={"single-service " + id} to={{
              pathname: `/services/${id}`,
              state: {
                fromParent: {name, id, description}
              }
            }}>
              <img src={image} alt="single-service-image"/>
              <span>{name}</span>
            </Link>
          ))}
        </li>
        <h2>Some companies we have worked with in the past:</h2>
        <div className="past-companies">
          <ul className="companies-list">
          {companyLogos.map(({image}) => (
            <img key={image} src={require(`./img/CompanyLogos/${image}`)} alt="company-logo" width="100px"/>
          ))}
          </ul>
        </div>
      </div>
      </div>
    )
  }
}
